mapper.update:
update student
name = #{name}, no = #{no}, gender = #{gender}, phone = #{phone}, degree = #{degree}, id_card = #{idCard}, is_college = #{isCollege}, address = #{address}, graduation_date = #{graduationDate}, violation_count = #{violationCount}, violation_score = #{violationScore}, clazz_id = #{clazzId}
where id = #{id}

mapper.select:
select s.*, c.name clazzName from student s left join clazz c on c.id = s.clazz_id


        and s.name like concat('%', #{name}, '%')


        and s.degree = #{degree}


        and s.class_id = #{classId}
